mapper.select:
SELECT DISEASE_SEQ , DISEASE_NAME , DISEASE_CONTENT , DISEASE_NAME_UN , DISEASE_CATEGORY , REMARK , DISEASE_SYMPTOM , DISEASE_CURE FROM DISEASE_LIST WHERE 1 = 1
AND DISEASE_CATEGORY = #{disease_category}
LIMIT #{row}

SELECT DISEASE_SEQ , DISEASE_NAME , DISEASE_CONTENT , DISEASE_NAME_UN , DISEASE_CATEGORY , REMARK , DISEASE_SYMPTOM , DISEASE_CURE FROM DISEASE_LIST WHERE 1 = 1
AND DISEASE_CATEGORY = #{disease_category}
AND ( DISEASE_NAME LIKE CONCAT_WS(#{searchKeyword},'%','%') OR UPPER(DISEASE_CONTENT) LIKE CONCAT_WS(#{searchKeyword},'%','%') OR UPPER(DISEASE_NAME_UN) LIKE CONCAT_WS(#{searchKeyword},'%','%') ) LIMIT #{row}

mapper.insert:
